const GalleryData = () => {
    return ( 
        {
            id:1,
            titile:"car",
            image:"./images/a1.jpg"
        },
        {
            id:2,
            titile:"car",
            image:"./images/a2.jpg"
        },
        {
            id:3,
            titile:"car",
            image:"./images/a3.jpg"
        },
        {
            id:4,
            titile:"car",
            image:"./images/a4.jpg"
        },
        {
            id:5,
            titile:"bike",
            image:"./images/b1.jpg"
        },
        {
            id:6,
            titile:"bike",
            image:"./images/b2.jpg"
        },
        {
            id:7,
            titile:"cycle",
            image:"./images/c1.jpg"
        },
        {
            id:8,
            titile:"cycle",
            image:"./images/c2.jpg"
        }
     );
}
 
const Gallery = () => {
    
    const [data,setData] = useState([]);
    const [collection,setCollection] = useState([]);
  
    useEffect(()=>{
        setData(GalleryData);
      setCollection([... new Set(GalleryData.map((item)=> item.titile))])
    },[]) 
    
    const gallery_filter = (itemData) =>{
      const filterData = GalleryData.filter((item)=> item.titile == itemData);
      setData(filterData);
    }
  
    return (
        <div className="App">
        <div className="galleryWrapper">
          <div className="filterItem">
            <ul>
              <li><button onClick={()=> setData(GalleryData)}>All</button></li>
              {
                collection.map((item)=> <li><button onClick={()=>{gallery_filter(item)}}>{item}</button></li>)
              }
            </ul>
          </div>
          <div className="galleryContainer">
            {
                data.map((item)=> <div  key={item.id} className="galleryItem"><img src={item.image  } /></div> )
            }
          </div>
        </div>
      </div>
    );
  }  
  
  


  export default GalleryData;
